/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from "react";
import img from "../foodTable/assets/index";

class CartItem extends Component {
  render() {
    function formatPrice(n, currency, fix = 1) {
      if (!currency) currency = "";
      if (!n || n === 0) return 0 + ` ${currency}`;
      return (
        n.toFixed(fix).replace(/(\d)(?=(\d{3})+\.)/g, "$1,") + " " + currency
      );
    }

    var { cart } = this.props;
    return (
      <tr>
        <td style={{ verticalAlign: "middle" }}>
          <img
            src={"http://dboi.info" + cart.product.avatar.url}
            alt=""
            className="img-fluid z-depth-0"
            style={{ width: "100px", height: "100px" }}
          />
        </td>
        <td style={{ verticalAlign: "middle" }}>
          <h5>
            <strong>{cart.product.name}</strong>
          </h5>
        </td>
        <td style={{ verticalAlign: "middle" }}>{formatPrice(cart.product.price)}đ</td>
        <td
          className="center-on-small-only"
          style={{ verticalAlign: "middle" }}
        >
          <div className="btn-group radio-group" data-toggle="buttons">
            <label
              className="btn btn-sm btn-primary
                                        btn-rounded waves-effect waves-light"
              style={{ backgroundColor: "green", color: "white" }}
              onClick={() => this.changSoLuong(cart.product, cart.soluong - 1)}
            >
              <a style={{ cursor: "pointer" }}>—</a>
            </label>
            <span
              className="qty"
              style={{
                textAlign: "center",
                width: "30px",
                height: "28px",
                backgroundColor: "white",
              }}
            >
              {cart.soluong}
            </span>
            <label
              className="btn btn-sm 
                        btn-rounded waves-effect waves-light"
              style={{ backgroundColor: "green", color: "white" }}
              onClick={() => this.changSoLuong(cart.product, cart.soluong + 1)}
            >
              <a style={{ cursor: "pointer" }}>+</a>
            </label>
          </div>
        </td>
        <td style={{ verticalAlign: "middle" }}>
          {formatPrice(this.total(cart.product.price, cart.soluong))}đ
        </td>
        <td style={{ verticalAlign: "middle" }}>
          <button
            type="button"
            className="btn btn-sm waves-effect waves-light"
            data-toggle="tooltip"
            data-placement="top"
            title=""
            data-original-title="Remove item"
            style={{ backgroundColor: "red", color: "white" }}
            onClick={() => this._onDelete(cart.product.id)}
          >
            X
          </button>
        </td>
      </tr>
    );
  }

  changSoLuong = (product, soluong) => {
    if (soluong <= 0) {
      this.props.onUpdateSoLuong(product, (soluong = 1));
    } else {
      this.props.onUpdateSoLuong(product, soluong);
    }
  };

  _onDelete = (product) => {
    this.props.onDeleteCart(product);
  };

  total = (price, sl) => {
    return price * sl;
  };
}

export default CartItem;
