import React, { Component } from "react";
import "../../App.css";
import { Link } from "react-router-dom";
import { connect } from "react-redux";

class Navmenu extends Component {
  componentDidMount() {
    this.props.getData();
  }

  render() {
    const { categories } = this.props;

    return (

      <nav className="navbar navbar-expand-sm fixed-top navbar-light rounded" style={{backgroundColor: 'white'}}>
      <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  
        <div className="collapse navbar-collapse justify-content-md-center" id="navbarSupportedContent" style={{fontSize: "18px"}}>
            <ul className="navbar-nav">
            <li className="nav-item active">
                    <Link className="nav-link" to="/">Trang chủ</Link>
                </li>
              <li className="nav-item dropdown ml-3">
                <Link className="nav-link dropdown-toggle" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                  Sản phẩm
                </Link>
                <div className="dropdown-menu dropdwn-content mt-3" aria-labelledby="navbarDropdown" >
                {!!categories &&
                  categories.map((cate, index) => {
                    return (
                      <Link className="dropdown-item" key={index} to={`/foodTable/${cate.id}`}>
                        {cate.name}
                      </Link>
                    );
                  })}
                </div>
              </li>
                
                <li className="nav-item ml-3">
                    <Link className="nav-link" to="/cart">Giỏ hàng</Link>
                </li>
                <li className="nav-item ml-3">
                    <Link className="nav-link" to="/danhnhap/login">Đăng nhập</Link>
                </li>
                <li className="nav-item ml-3">
                <Link className="nav-link" to="/history">History</Link>
            </li>
                <li className="nav-item ml-3">
                    <Link className="nav-link admin" to="/admin">Admin</Link>
                </li>
             
            </ul>

        </div>
     <form class="form-inline my-2 my-lg-0 navbar-cart">
      <label><i class="fas fa-cart-plus navCartItem"></i> Cart</label><span class="numberCart">3</span>
    </form>
</nav>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    categories: state.categories.data,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    getData: () => dispatch({ type: "CATE_GET_REQUEST" }),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Navmenu);
