import { USER_ADD_SUCCES } from "../../types/user";

var data = localStorage.getItem("LOGINADMIN");
const defaultState = {
  isLoginAdmin: data,
  mess: null
}
const login = (state = defaultState, action) => {
  switch (action.type) {
    case USER_ADD_SUCCES:
      localStorage.setItem("LOGINADMIN", action.payload.user.access_token);
      return {
        isLoginAdmin: action.payload.user.access_token,
      };

    case LOGOUT_ADMIN:
      localStorage.removeItem("LOGINADMIN");
      return {
          isLoginAdmin: null
        }  
    default:
      return state;
  }
};

export default login;
