import { LOGIN_ADMIN_SUCCESS } from "../../types/loginAdmin";

var data = localStorage.getItem("LOGINADMIN");
const defaultState = {
  isLoginAdmin: data.access_token,
  mess: null
}
const login = (state = defaultState, action) => {
  switch (action.type) {
    case LOGIN_ADMIN_SUCCESS:
      console.log(state.isLoginAdmin)
      localStorage.setItem("LOGINADMIN", action.payload.user.access_token);
      return {
        isLoginAdmin: action.payload.user.access_token,
      };
    default:
      return state;
  }
};

export default login;
