import * as React from 'react';
import { Route, Redirect, withRouter } from 'react-router-dom';
import {connect} from 'react-redux';

class PrivateRouter extends React.Component {
  renderComponent = () => {
    let { component: Component, childProps, ...rest } = this.props;
    return (<Component { ...childProps } {...rest}/>);
  };

  redirect = (url) => {
    return (<Redirect to={ url } />);
  }

  render() {
    const {isLoginAdmin} = this.props;
    const signedIn = () =>{
      var value = null;
      if(isLoginAdmin)
      {
        value = true
      }
      else{
        value = false
      }
      return value;
    }
    let {redirect, component, childProps, ...rest } = this.props;
    return ( <Route { ...rest } render={ props => signedIn() ? this.renderComponent() : this.redirect(redirect) } />);
  }
}

const mapStateToProps = (state) =>{
  return {
    isLoginAdmin: state.loginAdmin.isLoginAdmin
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(PrivateRouter);
