import React, { Component } from "react";
import CartItem from "./cartItem";
import CartResult from "./cartresult";
import { connect } from "react-redux";

class Cart extends Component {

  componentDidMount(){
    this.props.onGetCartData();
  }

  render() {
    const { cart } = this.props;
    console.log(cart);
    return (
      <section
        className="section"
        style={{ paddingBottom: "50px", paddingTop: "50px", marginTop: "50px" }}
      >
        <div className="container">
          <div className="">
            <table
              className="table product-table"
              style={{
                borderRadius: "5px solid white",
                backgroundColor: "white",
              }}
            >
              <thead>
                <tr style={{backgroundColor: '#0c713d', color: 'white'}}>
                  <th style={{ paddingLeft: "30px" }}>Hình ảnh</th>
                  <th>Sản Phẩm</th>
                  <th style={{ paddingLeft: "20px" }}>Giá</th>
                  <th style={{ paddingLeft: "20px" }}>Số Lượng</th>
                  <th>Tổng Cộng</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {cart.map((e, index) => {
                  return (
                    <CartItem
                      key={index}
                      cart={e}
                      onDeleteCart={this.onDeleteCart}
                      onUpdateSoLuong={this.onUpdateSoLuong}
                    />
                  );
                })}
                <CartResult cartresult={cart} />
              </tbody>
            </table>
          </div>
        </div>
      </section>
    );
  }

  onDeleteCart = (id) => {
    this.props.onDeleteCart(id);
  };

  onUpdateSoLuong = (product, soluong) => {
    this.props.onUpdateSoLuong(product, soluong);
  };
}

const mapStatetoProps = (state) => {
  return {
    cart: state.cart.data,
  };
};

const mapDispatchtoProps = (dispatch, props) => {
  return {
    onDeleteCart: (id) => dispatch({ type: "DELETE_PRODUCT_CART", id: id }),
    onUpdateSoLuong: (product, soluong) =>
      dispatch({
        type: "UPDATE_SOLUONG_CART",
        product: product,
        soluong: soluong,
      }),
    onGetCartData:() => dispatch({type: 'GET_CART_ITEMS_REQUEST'})
  };
};

export default connect(mapStatetoProps, mapDispatchtoProps)(Cart);
