import { takeLatest, put, all } from "redux-saga/effects";
import axios from "axios";
import { toast } from 'react-toastify';

function* getDataLogin({ email, password }) {
  const notifyfaile = () => toast.info(
    "Sai tên đăng nhập hoặc mật khẩu",{
    position: "top-right",
    autoClose: 3000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined,
    });
  
    const notifysucces = () => toast.info(
      "Đăng nhập thành công",{
      position: "top-right",
      autoClose: 3000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
      });

  try {
    console.log(email, password);
    const res = yield axios.post("http://dboi.info/api/sign_in", { email: email, password: password, });
    if (res.data.data.user.role === "admin") {
      const { data } = res.data;
      console.log(data);
      yield put({ type: LOGIN_M, payload: data});
      notifysucces()
    } else {
      console.log("Không có quyền truy cập");
    }
  } catch (error) {
    console.log(error.response.status)
    if(error.response.status === 401)
    {
      localStorage.removeItem("LOGINADMIN");
    }
    notifyfaile()
  }
}


function* getLogin() {
  yield takeLatest(LOGIN_ADMIN_REQUEST, getDataLogin);
}

export default function* () {
  yield all([getLogin()]);
}
