import { LOGIN_SUCCESS } from "../../types/login";

var data = localStorage.getItem("LOGIN");
const defaultState = {
  isLogin: data === 'true',
  mess: null
}
const login = (state = defaultState, action) => {
  switch (action.type) {
    case LOGIN_SUCCESS:
      console.log(state);
      localStorage.setItem("LOGIN", action.isLogin);
      return {
        isLogin: action.isLogin,
        mess: action.mess,
      };
    default:
      return state;
  }
};

export default login;
