import { takeLatest, put, all } from "redux-saga/effects";
import axios from "axios";

import { GET_CART_ITEMS_SUCCESS, GET_CART_ITEMS_REQUEST, ADD_CART_ITEMS_SUCCESS, ADD_CART_ITEMS_REQUEST } from "../types/cart";

//get
function* getCartData() {
  var token = localStorage.getItem("LOGINMEMBER");
  axios.defaults.headers.common['AUTH-TOKEN'] = token;
  const res = yield axios.get("http://dboi.info/api/items");
  console.log(res)
  if (res.status === 200) {
    const { data } = res.data;
    yield put({ type: GET_CART_ITEMS_SUCCESS, payload: data });
  }
}

function* getCartDataRequest() {
  yield takeLatest(GET_CART_ITEMS_REQUEST, getCartData);
}

//add
function* addCartItems(itemCart) {
  console.log(itemCart)
  var token = localStorage.getItem("LOGINMEMBER");
  axios.defaults.headers.common['AUTH-TOKEN'] = token;
  const res = yield axios.post("http://dboi.info/api/items", itemCart.item );
  console.log(res)
  if (res.status === 200) {
    const { data } = res.data;
    yield put({ type: ADD_CART_ITEMS_SUCCESS, payload: data });
  }
}

function* addCartItemsRequest() {
  yield takeLatest(ADD_CART_ITEMS_REQUEST, addCartItems);
}


export default function* () {
  yield all([getCartDataRequest(),addCartItemsRequest()]);
}
