import React, { Component } from "react";

class Register extends Component {

  constructor(props){
    super(props);
    this.state={
      txtFullName: '',
      txtEmail: '',
      txtPhone: null,
      txtBirthDate: '',
      txtPass: ''
    }
  }

  _onChange = (e) =>{
    var target= e.target;
    var name= target.name;
    var value = target.value;
  }

  render() {
    const {txtPhone,txtFullName,txtEmail,txtBirthDate,txtPass} = this.state;
    return (
      <div
        className="container-fluid pt-1"
        style={{
          backgroundColor: "#efefef",
          marginTop: "70px",
          paddingTop: "20px",
          paddingBottom: "10px",
        }}
      >
        <div
          className="container"
          style={{
            backgroundColor: "#ffffff",
            borderRadius: "5px",
            marginTop: "20px",
            marginBottom: "20px",
          }}
        >
            <form className="form-DK" >
              <h1>Thông tin khách hàng</h1>
              <div className="form-row" style={{justifyContent: 'center'}}>
                <div className="form-group col-md-4">
                  <label className="la">
                    Email <span>(*)</span>
                  </label>
                  <input type="text" className="form-control" name="txtEmail" value={txtEmail} onChange= {this._onChange}/>
                </div>

                <div className="form-group col-md-4">
                  <label className="la">
                    Họ và Tên<span>(*)</span>
                  </label>
                  <input type="text" className="form-control" name="txtFullName" value={txtFullName} onChange= {this._onChange}/>
                </div>
              </div>
              <div className="form-row" style={{justifyContent: 'center'}}>
              <div className="form-group col-md-4">
              <label className="la">
                Ngày Sinh<span>(*)</span>
              </label>
              <input type="date" className="form-control" name="txtBirthDate" value={txtBirthDate} onChange= {this._onChange}/>
            </div></div>
              <div className="form-row" style={{justifyContent: 'center'}}>
                <div className="form-group col-md-4">
                  <label className="la">
                    Số điện thoại <span>(*)</span>
                  </label>
                  <input type="text" className="form-control" name="txtPhone" value={txtPhone} onChange= {this._onChange}/>
                </div>

                <div className="form-group col-md-4">
                  <label className="la">
                    Mật khẩu<span>(*)</span>
                  </label>
                  <input type="password" className="form-control" name="txtPass" value={txtPass} onChange= {this._onChange}/>
                </div>
              </div>
            </form>
          </div>
      </div>
    );
  }
}

export default Register;
