import { LOGIN_MEMBER_SUCCESS } from "../../types/loginMember";


var data = localStorage.getItem("LOGINMEMBER");
const defaultState = {
  isLoginMember: data
}
const login = (state = defaultState, action) => {
  switch (action.type) {
    case LOGIN_MEMBER_SUCCESS:
      console.log(action)
      localStorage.setItem("LOGINMEMBER", action.payload.user.access_token);
      return {
        isLoginMember: action.payload.user.access_token,
      };

    // case LOGIN_MEMBER_REQUEST:
    //   console.log(action)
      // localStorage.removeItem("LOGINADMIN");
      // return {
      //     isLoginAdmin: null
      //   } 
      // return state;
    default:
      return state;
  }
};

export default login;
