/* eslint-disable jsx-a11y/no-redundant-roles */
import React, { Component } from "react";
import { Link } from "react-router-dom";

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      txtFullName: "",
      txtEmail: "",
      txtPhone: "",
      txtBirthDate: "",
      txtPass: "",
      txtUserName: "",
    };
  }

  _onChange = (e) => {
    var target = e.target;
    var name = target.name;
    var value = target.value;
    this.setState({
      [name]: value,
    });
    console.log(this.state);
  };

  _onSubmit = (e) => {
    e.preventDefault();
    var dataUser = {
      email: this.state.txtEmail,
      password: this.state.txtPass,
      user_name: this.state.txtUserName,
      full_name: this.state.txtFullName,
      phone: this.state.txtPhone,
      birthday: this.state.txtBirthDate,
    };
  };

  render() {
    const {
      txtPhone,
      txtFullName,
      txtEmail,
      txtBirthDate,
      txtPass,
      txtUserName,
    } = this.state;
    return (
      <div
        className="container-fluid pt-1"
        style={{
          backgroundColor: "#efefef",
          marginTop: "70px",
          paddingTop: "20px",
          paddingBottom: "10px",
        }}
      >
        <div
          className="container"
          style={{
            backgroundColor: "#ffffff",
            borderRadius: "5px",
            marginTop: "20px",
            marginBottom: "20px",
          }}
        >
          <form className="form-DK">
            <h1>Thông tin khách hàng</h1>
            <div className="form-row" style={{ justifyContent: "center" }}>
              <div className="form-group col-md-4">
                <label className="la">
                  Email <span>(*)</span>
                </label>
                <input
                  type="text"
                  className="form-control"
                  name="txtEmail"
                  value={txtEmail}
                  onChange={this._onChange}
                />
              </div>

              <div className="form-group col-md-4">
                <label className="la">
                  Họ và Tên<span>(*)</span>
                </label>
                <input
                  type="text"
                  className="form-control"
                  name="txtFullName"
                  value={txtFullName}
                  onChange={this._onChange}
                />
              </div>
            </div>
            <div className="form-row" style={{ justifyContent: "center" }}>
              <div className="form-group col-md-4">
                <label className="la">
                  Ngày Sinh<span>(*)</span>
                </label>
                <input
                  type="date"
                  className="form-control"
                  name="txtBirthDate"
                  value={txtBirthDate}
                  onChange={this._onChange}
                />
              </div>
              <div className="form-group col-md-4">
                <label className="la">
                  Tên người dùng<span>(*)</span>
                </label>
                <input
                  type="text"
                  className="form-control"
                  name="txtUserName"
                  value={txtUserName}
                  onChange={this._onChange}
                />
              </div>
            </div>
            <div className="form-row" style={{ justifyContent: "center" }}>
              <div className="form-group col-md-4">
                <label className="la">
                  Số điện thoại <span>(*)</span>
                </label>
                <input
                  type="text"
                  className="form-control"
                  name="txtPhone"
                  value={txtPhone}
                  onChange={this._onChange}
                />
              </div>

              <div className="form-group col-md-4">
                <label className="la">
                  Mật khẩu<span>(*)</span>
                </label>
                <input
                  type="password"
                  className="form-control"
                  name="txtPass"
                  value={txtPass}
                  onChange={this._onChange}
                />
              </div>
            </div>
            <button   style={{ justifyContent: "center" }} className="btn btn-primary ml-2" role="button">
            </button>
            <Link to="" className="btn btn-danger">Hủy bỏ</Link>
          </form>
        </div>
      </div>
    );
  }
}

export default Register;
