import { LOGIN_SUCCESS } from "../../types/loginAdmin";

var data = localStorage.getItem("LOGINUSER");
const defaultState = {
  isLogin: data === 'true',
  mess: null
}
const member = (state = defaultState, action) => {
  switch (action.type) {
    case LOGIN_SUCCESS:
      console.log(state);
      localStorage.setItem("LOGINUSER", action.isLogin);
      return {
        isLogin: action.isLogin,
        mess: action.mess,
      };
    default:
      return state;
  }
};

export default member;
