import {
  DELETE_PRODUCT_CART,
  UPDATE_SOLUONG_CART,
  GET_CART_ITEMS_SUCCESS,
  DELETE_CART_ITEMS_SUCCESS,
} from "../../types/cart";

var defaultState = {
  data: []
};

const card = (state = defaultState, action) => {
  var { product, soluong } = action;
  var index = -1;
  switch (action.type) {

    case GET_CART_ITEMS_SUCCESS:
      return {
        ...state,
        data: action.payload.item
      }
      

    //thêm
    // case ADD_PRODUCT_CART:
    //   index = findId(state, product.id);
    //   if (index !== -1) {
    //     state[index].soluong += soluong;
    //   } else {
    //     state.push({
    //       product: product,
    //       soluong: soluong,
    //     });
    //   }
    //   localStorage.setItem("CART", JSON.stringify(state)); 
    //   return [...state];

    case DELETE_CART_ITEMS_SUCCESS:
      index = findId(state, action.payload);
      if (index !== -1) {
        state.splice(index, 1);
      }
      return [...state];

      //update
    case UPDATE_SOLUONG_CART:
      index = findId(state, product.id);
      if (index !== -1) {
        state[index].soluong = soluong;
      }
      localStorage.setItem("CART", JSON.stringify(state));
      return [...state];
    default:
      return state;
  }
};


//
var findId = (card, products) => {
  var index = -1;
  for (var i = 0; i < card.length; i++) {
    if (card[i].product.id === products) {
      index = i;
      break;
    }
  }
  return index;
};

export default card;
