import { takeLatest, put, all } from "redux-saga/effects";

import { LOGIN_SUCCESS, LOGIN_REQUEST } from "../types/login";

function* getDataLogin({ user, pass }) {
  if (user === "admin" && pass === "admin") {
    yield put({ type: LOGIN_SUCCESS, isLogin: true, mess: null });
  } else {
    yield put({
      type: LOGIN_SUCCESS,
      isLogin: false,
      mess: "Đăng nhập không thành công!",
    });
  }
}

function* getLogin() {
  yield takeLatest(LOGIN_REQUEST, getDataLogin);
}

export default function* () {
  yield all([getLogin()]);
}
