import { LOGIN_ADMIN_SUCCESS } from "../../types/loginAdmin";

var data = localStorage.getItem("LOGINADMIN");
const defaultState = {
  isLogin: data === 'true',
  mess: null
}
const login = (state = defaultState, action) => {
  switch (action.type) {
    case LOGIN_ADMIN_SUCCESS:
      console.log(state);
      localStorage.setItem("LOGINADMIN", action.isLogin);
      return {
        isLogin: action.isLogin,
        mess: action.mess,
      };
    default:
      return state;
  }
};

export default login;
