import { LOGIN_ADMIN_SUCCESS } from "../../types/loginAdmin";

var data = localStorage.getItem("LOGINADMIN");
const defaultState = {
  isLoginAdmin: data === 'true',
  mess: null
}
const login = (state = defaultState, action) => {
  switch (action.type) {
    case LOGIN_ADMIN_SUCCESS:
      console.log(action,payload)
      localStorage.setItem("LOGINADMIN", action);
      return {
        isLogin: action.payload,
      };
    default:
      return state;
  }
};

export default login;
